import React,{Component} from 'react';
import {Container,ListGroupItem,Button, ListGroup} from 'reactstrap';
import {CSSTransition,TransitionGroup} from 'react-transition-group';
import uuid from 'react-uuid';
class UserList extends Component{
state={
users:[
    {id:uuid(),name:'adam',mdp:'123',solde:'500'},
    {id:uuid(),name:'jhon',mdp:'64',solde:'9274'},
    {id:uuid(),name:'ahmed',mdp:'6459',solde:'550'}


]
}
render(){
    const {users}=this.state;
    return (
        <Container>
            <Button
            color="dark"
            style={{marginBottom:'2rem'}}
            onClick ={()=>{
                const name=prompt ('Entrer name');
                const mdp=prompt ('Entrer mdp');
                const solde=prompt ('Entrer solde');
                this.setState(state=>({users:[...state.users,{id :uuid(),name,mdp,solde}]}))
            }}>
                Add user </Button>
<ListGroup>
    <TransitionGroup className="User-List">
        {users.map(({id,name,mdp,solde})=>(
            
            <ListGroupItem key={id}>{name},{mdp},{solde}</ListGroupItem>
            )
        )}
    </TransitionGroup>
</ListGroup>
</Container>
    );

}
}
export default UserList;
