import {GET_USERS,ADD_USER, DELETE_USER,USERS_LOADING,PUT_USER} from '../actions/types';
const initialState={
    users:[],
    loading:false
}
export default function (state =initialState,action){
    switch(action.type){
        case GET_USERS:
            return{
                ...state,
                users:action.payload,
                loading:false,
            };
        case DELETE_USER :
        return{
            ...state,
            users:state.users.filter(user=>user._id!==action.payload)
        }   ;
        case ADD_USER:
            return{
                ...state,
                users:[action.payload,...state.users]
            };
        case USERS_LOADING:
            return{
                ...state,
                loading:true
            }    ;
            case PUT_USER:
                return{
                    ...state,
                    users: state.users.map(
                      user => 
                        user.name === action.payload.name
                          
                          ? action.payload
                          : user
                    )
                  };
              
            default:
                return state;
    }
}
