import React,{Component} from 'react';
import{
    Button,
    Modal,
    ModalHeader,
    Form,
    FormGroup,
    Label,
    Input,
    ModalBody
} from 'reactstrap';
 import {connect} from 'react-redux';
 import {updateUser} from  '../actions/userActions';
 class InterfaceUser extends Component{
  state={
      modal:false,
      name:'',
      mdp:'',
      solde:'',
  }
  toggle=()=>{
      this.setState({
          modal:!this.state.modal
      });
  
  }
  onChange=(e)=>{
    this.setState({[e.target.name]:e.target.value});

};
onSubmit = e =>{
  e.preventDefault();
  const user={

name:this.state.name,

solde:this.state.solde,

  }
  this.props.updateUser(user);
  
}
  render(){
    

    
        
               
      return(
          <div>
               
<Form onSubmit={this.onSubmit}>
<FormGroup controlId="formBasicEmail">
  <Label>solde</Label>
  <Input type="text" name="solde" id="user" placeholder="Entre solde" onChange={this.onChange} />
  
</FormGroup>


<Button variant="primary" type="submit" className='mr-3'>
  verser
</Button>
</Form>
<Modal
  isOpen={this.state.modal}
  toggle={this.toggle}>

      <ModalHeader toggle={this.toggle}>
          nom destinataire
      </ModalHeader>
      <ModalBody>
          <Form onSubmit={this.onSubmit}>
          
          <FormGroup>
                  <Label for="user">User</Label>
                  <Input 
                  type="text"
                  name="name"
                  id="user"
                  placeholder="Add name"
                  onChange={this.onChange}/>

              </FormGroup>
              </Form>
              </ModalBody>
              </Modal>

</div>)}}
const mapStateToProps = state =>({
  user : state.user
});

export default connect (mapStateToProps,{updateUser}) (InterfaceUser);
