import uuid from 'react-uuid';
import {GET_USERS,ADD_USERS,DELETE_USERS} from '../actions/types';
const initialState={
    users:[
        {id:uuid(),name:'adam',mdp:'123',solde:'500'},
        {id:uuid(),name:'jhon',mdp:'64',solde:'9274'},
        {id:uuid(),name:'jmal',mdp:'6459',solde:'550'}
    
    
    ]
}
export default function (state =initialState,action){
    switch(action.type){
        case GET_USERS:
            return{
                ...state
            };
            default:
                return state;
    }
}
