import React,{Component} from 'react';
import {Container,ListGroupItem,Button, ListGroup} from 'reactstrap';
import {CSSTransition,TransitionGroup} from 'react-transition-group';
import {connect} from 'react-redux';
import {getUsers,deleteUser}from '../actions/userActions';
import PropTypes from 'prop-types'




class UserList extends Component{


componentDidMount(){
    this.props.getUsers();
}
onDeleteClick=(id)=>{
    this.props.deleteUser(id);
}

render(){
    
    const {users}=this.props.user;
    return (
        <Container>
            <Button
            color="dark"
            style={{marginBottom:'2rem'}}
            onClick ={()=>{
                const name=prompt ('Entrer name');
                const mdp=prompt ('Entrer mdp');
                const solde=prompt ('Entrer solde');
                this.setState(state=>({users:[...state.users,{id :uuid(),name,mdp,solde}]}))
            }}>
                Add user </Button>
<ListGroup>
    <TransitionGroup className="User-List">
        {users.map(({id,name,mdp,solde})=>(
           <CSSTransition key={id} timeout={500} classNames="fade">
            <ListGroupItem >
                <Button className="remove-btn"
                color="danger"
                size="sm"
                onClick={this.onDeleteClick.bind(this,id)}>&times;</Button>
                
                
                <h5>Name:</h5>{name}<h5 >MDP:</h5>{mdp}<h5 >Solde:</h5>{solde}</ListGroupItem></CSSTransition>
            ))}
    </TransitionGroup>
</ListGroup>
</Container>
    );

}
}
UserList.propTypes = {
    getUsers :PropTypes.func.isRequired,
    user:PropTypes.object.isRequired
}

const mapStateToProps =(state)=>({
    user:state.user
})




export default connect(mapStateToProps,{getUsers,deleteUser}) (UserList);
