import React,{Component} from 'react';
import{
    Button,
    Modal,
    ModalHeader,
    Form,
    FormGroup,
    Label,
    Input,
    ModalBody
} from 'reactstrap';
 import {connect} from 'react-redux';
 import {updateUser} from  '../actions/userActions';
 class InterfaceUser extends Component{
    state={
        modal:false,
        name:'',
        mdp:'',
        solde:'',
        email:''
    }
    toggle=()=>{
        this.setState({
            modal:!this.state.modal
        });
    
    }
    onChange=(e)=>{
      this.setState({[e.target.name]:e.target.value});
  };
    render(){
        return(
            <div>
<Form>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>solde</Form.Label>
    <Form.Control type="text" placeholder="Entre solde"  />
    
  </Form.Group>
  </Form>
  
  <Button variant="primary" type="submit">
    verser
  </Button>
  <Button variant="primary" type="submit">
    retirer
  </Button>
  <Button variant="primary" type="submit" onClick={this.toggle}
>
    virer
  </Button>
  <Modal
    isOpen={this.state.modal}
    toggle={this.toggle}>

        <ModalHeader toggle={this.toggle}>
            nom destinataire
        </ModalHeader>
        <ModalBody>
            <Form onSubmit={this.onSubmit}>
            
            <FormGroup>
                    <Label for="user">User</Label>
                    <input 
                    type="text"
                    name="name"
                    id="user"
                    placeholder="Add name"
                    onChange={this.onChange}/>

                </FormGroup>
                </Form>
                </ModalBody>
                </Modal>

</div>)}}
const mapStateToProps = state =>({
    user : state.user
});

 export default connect (null) (InterfaceUser);
