        <Fragment>
            <Container>

<UserModal/>
<UserList/>
</Container> 
        </Fragment>
    );



  return (
    <Provider store={store}>
     <div className = "App" >
    <AppNavbar/>  
    {isAuthenticated? authLinks:''}

    </div>
    </Provider>



  );
}}

export default App;
